package Arrays2_D;

public class Array1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int[][] arr=new int[2][3];
		arr[0][0]=1;
		arr[0][1]=2;
		arr[0][2]=3;
		arr[1][0]=4;
		arr[1][1]=5;
		arr[1][2]=6;
		System.out.println("This is 2D array : ");
		System.out.println("Value at index[0][0]="+arr[0][0]);
		System.out.println("Value at index[0][1]="+arr[0][1]);
		System.out.println("Value at index[0][2]="+arr[0][2]);
		System.out.println("Value at index[1][0]="+arr[1][0]);
		System.out.println("Value at index[1][1]="+arr[1][1]);
		System.out.println("Value at index[1][2]="+arr[1][2]);
	}

}
